<?php
$user = getUser();
if(!$user){
    header('Location: /login');
}
$user_id = $user['user_id'];

$readonly = !(isset($_SESSION['user']) && $_SESSION['user']['user_id'] == $user_id);
$query = false;
if(isset($_REQUEST['save_profile']) && !$readonly){
    $companyName = $_REQUEST['company_name'];
    $telephone = preg_replace("/[^0-9]/",'', $_REQUEST['telephone']);
    $companyHours = $_REQUEST['company_hours'];
    $companyCity = $_REQUEST['company_city'];
    $companyAddress = $_REQUEST['company_address'];
    $companyZip = $_REQUEST['company_zip'];
    $name = $_REQUEST['name'];
    $surname = $_REQUEST['surname'];
    $email = $_REQUEST['email'];
    $card = str_replace(' ', '', $_REQUEST['card']);
    $card_name = $_REQUEST['card_name'];
    $card_date = $_REQUEST['card_date'];
    $card_cvv = $_REQUEST['card_cvv'];
    $profile_verify = $_FILES['profile_verify'];
    $query = UserController::update($user['user_id'], $companyName, $companyHours, $companyCity, $companyAddress, $companyZip, $telephone, $name, $surname, $email, $card, $card_name, $card_date, $card_cvv, $profile_verify);
    $user = User::get($user['user_id']);
}
